import React, { useState, useEffect, useRef } from 'react';
import { motion } from "framer-motion";
import Footer from './Footer';

const Home = () => {
  const ref = useRef(0);
  const [text, setText] = useState('');

  useEffect(() => {
    const interval = setInterval(() => {
      if (ref.current < name.length) {
        ref.current++;
        setText((prevText) => prevText + name[ref.current - 1]);
      }
    }, 200);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className='area relative z-0 bg-black w-screen h-screen'>
      {/* Background Circles */}
      <ul className="circles">
        <li></li>
        <li></li>
        <li></li>
        <li></li>
        <li></li>
        <li></li>
        <li></li>
        <li></li>
        <li></li>
        <li></li>
      </ul>

      {/* Hero Section */}
      <div className='hero relative h-[calc(100vh)] flex flex-col justify-center items-center text-white'>
        <motion.div 
          className='backdrop-blur-sm rounded-3xl p-8 text-center'
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1, ease: "easeInOut" }}
        >
          <motion.h1 
            className='text-6xl sm:text-7xl font-extrabold mt-2'
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, ease: "easeInOut", delay: 1 }}
          >
            Hi, I'm&nbsp;
            <motion.span 
              className='text-yellow-200 font-extrabold'
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.5, ease: "easeInOut", delay: 1.5 }}
            >
              {text}
            </motion.span>
          </motion.h1>
          <motion.p 
            className='mt-3 text-xl'
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, ease: "easeInOut", delay: 2 }}
          >
            "I Love Bringing Projects to Life with Precision and Purpose, Ensuring Every Detail Aligns with the Bigger Picture."
          </motion.p>
        </motion.div>
      </div>

      <Footer />
    </div>
  );
}

export default Home;
