import React from 'react';
import '../App.css';
import { services } from '../constants';
import ButtonLink from './ButtonLink';
import Footer from './Footer';

const ServiceCard = ({ service }) => (
  <div className='sm:w-[250px] w-full'>
    <div
      className='w-full green-pink-gradient p-[1px] rounded-[20px]'>
      <div
        className='rounded-[20px] py-5 px-12 min-h-[280px] flex justify-evenly items-center flex-col'
        style={{ background: '#151030' }}>
        <img
          src={service.icon}
          alt='some_icon'
          className='w-16 h-16 object-contain'
        />
        <h3 className='text-white text-[20px] font-bold text-center'>
          {service.title}
        </h3>
      </div>
    </div>
  </div>
);

const About = () => {
  return (
    <div>

      <div className='bg-black h-full w-full text-white sm:flex sm:justify-around about py-12 mt-8 overflow-x-hidden' id='about'>
        <div className='flex flex-col justify-around'>
          <div className='sm:px-16 px-2'>
            <h2 className='text-4xl sm:text-5xl font-extrabold mt-2'>Introduction</h2>
            <p className='mt-3 mb-6 text-[17px] max-w-3xl leading-[30px]'>üëã Hello! I‚Äôm Gayathri Chilkuri, a committed<a className='text-green-300 hover:text-green-500 duration-300' href='https://www.linkedin.com/in/gayathri-chilukuri-140439289/' target='_blank'> Project Management student </a>at Lewis University üë®‚Äçüíª.
              <br />
              ‚úçÔ∏è I thrive on transforming complex ideas into actionable plans and leading projects to successful completion. üåü My goal is to master the art of project management and drive impactful results through effective strategies and teamwork.üöÄ
              <br/>
              On a different note, I'm also into sports.I am a Hockey Senior Zonal üèí champion ü•á. Me and my mates have managed to get 3rd place in Basketball üèÄ at university level .</p>

            <ButtonLink
              url='https://drive.google.com/file/d/1hj2rz1BmbsjmSKGOzgFj3kXxgp1s9Sku/view?usp=sharing'
              text='View Resume ‚Üí'
              padding={`p-3`}
            />

          </div>
          <div className='mt-20 flex justify-center flex-wrap gap-7'>
            {services.map((service) => (
              <ServiceCard service={service} />
            ))}
          </div>
        </div>
      </div>
      <Footer />
    </div>
  )
}

export default About;
